taskexecutions.map:
function (doc) {
  if(doc.type == "taskexecution")
  emit([doc.program, doc.task, doc.timestamp], doc.output);
}

scope.map:
function (doc) {
  if(doc.type == "program") {
    if(doc.inscope.length > 0) {
      doc.inscope.forEach(function(scope) {
        emit([!doc.disabled, "IN", scope], doc._id)
      });
    }
    if(doc.outscope.length > 0) {
      doc.outscope.forEach(function(scope) {
        emit([!doc.disabled, "OUT", scope], doc._id)
      });
    }
  }

}

search_tags.map:
function (doc) {
  if(doc.tags) {
    for(prop in doc.tags) {
      emit([prop, doc.tags[prop]], [doc.type, doc._id, doc.program])
    }

  }
  if(doc.ips && doc.ips.length > 0) {
    for(ip in doc.ips) {
        if(doc.ips[ip].length > 0) emit(['ip', doc.ips[ip]], [doc.type, doc._id, doc.program])
    }
  }
  if(doc.domains) {
      for(domain in doc.domains) {
        if(doc.domains[domain].length > 0) emit(['domain', doc.domains[domain]], [doc.type, doc._id, doc.program])
      }
  }
  if(doc.type == 'service') {
    emit(['ip', doc.ip], [doc.type, doc._id, doc.program])
    emit(['service', doc.service], [doc.type, doc._id, doc.program])
    emit(['port', doc.port], [doc.type, doc._id, doc.program])
  }

  if(doc.source) {
    emit(['source', doc.source], [doc.type, doc._id, doc.program])
  }
}
